mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE mb_name = #{mb_name}
AND mb_emaile = #{mb_emaile}
AND mb_id = #{mb_id}

SELECT COUNT(*)
FROM MEMBER
WHERE mb_name = #{mb_name}
AND mb_emaile = #{mb_emaile}

SELECT mb_id
FROM MEMBER
WHERE mb_emaile = #{mb_emaile}
AND mb_name = #{mb_name}

mapper.update:
UPDATE MEMBER
SET mb_pswd = #{mb_pswd ,jdbcType=VARCHAR} 
,mb_emaile= #{mb_emaile,jdbcType=VARCHAR}
WHERE mb_id = #{mb_id,jdbcType=VARCHAR}
