mapper.update:
update post
set post_views = post_views +1
Where post_num_pk = #{post_num_pk}

mapper.delete:
DELETE Ap_post
WHERE mb_num_pk = #{mb_num_pk}
AND post_num_pk = #{post_num_pk}

mapper.select:
	
	 select *
  	 FROM 
  	 	(
		 SELECT rownum rn, p.post_num_pk, p.mb_num_pk, post_title
         , post_con, post_dline, post_numof, post_price, post_views
         , post_date, pb.pbigc_name||' '||ps.psmallc_name as plant
		 FROM POST p JOIN Ap_post ap  ON p.post_num_pk = ap.post_num_pk
		        JOIN psmallc ps ON p.psmallc_pk = ps.psmallc_pk
		        JOIN pbigc pb ON ps.pbigc_pk = pb.pbigc_pk
		 WHERE ap.mb_num_pk = #{mb_num_pk}
       	AND rownum <= #{pageNum} * #{amount}
      )
  where rn > (#{pageNum} -1) * #{amount}

select p.*, v.VH_DATE, s.PSMALLC_NAME, b.PBIGC_NAME, m.MB_ID
from member m, post p left join vlntr_his v on v.POST_NUM_PK = p.POST_NUM_PK,
(PSMALLC s LEFT OUTER JOIN PBIGC b ON s.PBIGC_PK = b.PBIGC_PK)
where p.POST_NUM_PK = #{post_num_pk}
and p.PSMALLC_PK = s.PSMALLC_PK
and p.MB_NUM_PK = m.MB_NUM_PK

mapper.update:
update post set POST_TITLE = #{post_title}, POST_CON = #{post_con}, POST_DLINE = #{post_dline},
 POST_NUMOF = #{post_numof}, PSMALLC_PK = #{psmallc_pk}, POST_PRICE = #{post_price}, POST_DATE = sysdate
where POST_NUM_PK = #{post_num_pk}
