mapper.select:
select p.POST_NUM_PK, p.POST_TITLE, p.POST_VIEWS, p.POST_DATE, m.MB_ID, s.PSMALLC_NAME, b.PBIGC_NAME
from post p, member m, (PSMALLC s LEFT OUTER JOIN PBIGC b ON s.PBIGC_PK = b.PBIGC_PK)
where p.MB_NUM_PK = m.MB_NUM_PK
and p.PSMALLC_PK = #{psmallc_pk}
and p.PSMALLC_PK = s.PSMALLC_PK
order by POST_DATE desc

select p.POST_NUM_PK, p.POST_TITLE, p.POST_VIEWS, p.POST_DATE, m.MB_ID, s.PSMALLC_NAME, b.PBIGC_NAME
from post p, member m, (PSMALLC s LEFT OUTER JOIN PBIGC b ON s.PBIGC_PK = b.PBIGC_PK)
where p.MB_NUM_PK = m.MB_NUM_PK
and p.PSMALLC_PK = s.PSMALLC_PK
and m.MB_ID = #{mb_id}
order by POST_DATE desc

	select p.POST_NUM_PK, p.POST_TITLE, p.POST_CON, p.POST_DLINE, p.POST_PRICE, p.POST_NUMOF, p.POST_VIEWS, p.POST_DATE, m.MB_ID, s.PSMALLC_NAME, b.PBIGC_NAME
	from post p, member m, (PSMALLC s LEFT OUTER JOIN PBIGC b ON s.PBIGC_PK = b.PBIGC_PK)
	where p.MB_NUM_PK = m.MB_NUM_PK
	and p.PSMALLC_PK = s.PSMALLC_PK
    and p.POST_NUM_PK = #{post_num_pk}
	order by POST_DATE desc

select * from ap_post
where POST_NUM_PK = #{post_num_pk}
and MB_NUM_PK = #{mb_num_pk}
